//Object Master
var Master = function (){
	//Variables
	var queue = [];

	//Start
	this.start = function (){
		//Comment System
		var commentService = new CommentMaster();
		commentService.start();

		//Exec queue script (for module)
		execQueueScripts();
	}


	//Queue Script system
	var execQueueScripts = function () {
		var q = getQueue();
		for( var o in q ) {
			console.log(q[o]);
			eval(q[o]);
		}
	}
	//Add a script to exec at masterClass.start()
	this.addQueue = function ( script ) {
		var q = getQueue();
		//console.log(q);
		
		q.push(script + ';');
		//console.log(q);
	}

	//return void
	var setQueue = function (a) {
		queue = a;
	}
	//return queue
	var getQueue = function () {
		return queue;
	}
}


